mapper.insert:


  SELECT LAST_INSERT_ID()

insert into t_sys_menu (`name`, `desc`, code, 
  pcode, url, isp, isopen, 
  authcode, icon, `status`
  )
values (#{name,jdbcType=VARCHAR}, #{desc,jdbcType=VARCHAR}, #{code,jdbcType=VARCHAR}, 
  #{pcode,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, #{isp,jdbcType=INTEGER}, #{isopen,jdbcType=INTEGER}, 
  #{authcode,jdbcType=VARCHAR}, #{icon,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )

mapper.select:

select id, `name`, `desc`, code, pcode, url, isp, isopen, authcode, icon, `status`
from t_sys_menu
where id = #{id,jdbcType=INTEGER}


SELECT id, `name`, `desc`, CODE, pcode, url, isp, isopen, authcode, icon, `status`
  FROM t_sys_menu WHERE authcode IN(
      SELECT authcode FROM `t_sys_admin_auth` WHERE username = #{loginName} AND STATUS=0
      UNION ALL
      SELECT authcode FROM `t_sys_role_auth` WHERE roleid IN(
        SELECT roleid FROM `t_sys_admin_role` WHERE username = #{loginName}
        )
  ) order by orders
